import React, { Component } from "react";
import {
  View,
  Text,
  FlatList,
  ActivityIndicator,
  SafeAreaView
} from "react-native";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loading: false,
      data: [],
      error: null
    };
  }

  componentDidMount() {
    this.makeBehaviorRequest();
  }

  makeBehaviorRequest = () => {
    this.setState({ loading: true });

    getBehaviors()
      .then(behaviors => {
        this.setState({ loading: false, data: behaviors });
      })
      .catch(error => {
        this.setState({ error, loading: false });
      });
  };

  clickHandler = () => {
    // search through indexCards
  }

  pressHandler = (key) => {
    console.log(key)
  }


  render() {
    return (
      <View>
        <SafeAreaView/>
        <Text>BehaviorBuddies</Text>

        <Text style={styles.searchText}>What should we learn about today?</Text>
        <TextInput 
          style={styles.input}
          placeholder='enter keyword(s)'
          // onChangeText={(val) => searchIndexCards(val)}
          />

        <View style={styles.buttonContainer}>
            <Button title='SEARCH' onPress={clickHandler}/>
        </View>

        <View>

          <FlatList
            data={indexCards}
            renderItem={({ item }) => (
              <TouchableOpacity onPress={() => pressHandler(item.key)}>
                <Text style={styles.indexCards}>{item.title}</Text>
              </TouchableOpacity>
            )}
          />

        </View>

        <StatusBar style="auto" />
      </View>
    );
  }
}

