import React, { Component } from "react";
import {
  View,
  Text,
  FlatList,
  ActivityIndicator,
  SafeAreaView
} from "react-native";



let baseURL = ''

if(process.env.NODE_ENV === 'development'){
  baseURL = 'http://localhost:3003'
} else {
  baseURL = 'your heroku backend url here, bub'
}



class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      behaviors: [],
    };
    this.getBehaviors = this.getBehaviors.bind(this)
  }

  componentDidMount() {
    this.getBehaviors();
  }


  getBehaviors() {
    fetch(baseURL + '/behavior_buddy')
      .then(data => { return data.json() }, err => console.log(err))
      .then(parsedData => this.setState({ behaviors: parsedData }), err => console.log(err))
  }

  clickHandler = () => {
    // search through indexCards
  }

  pressHandler = (key) => {
    console.log(key)
  }


  render() {
    return (
      <View>
        <SafeAreaView/>
        <Text>BehaviorBuddies</Text>

        <Text>What should we learn about today?</Text>
        {/* <TextInput 
          placeholder='enter keyword(s)'
          // onChangeText={(val) => searchIndexCards(val)}
          /> */}

        <View>
            {/* <Button title='SEARCH' onPress={clickHandler}/> */}
        </View>

        <View>

          <FlatList
            // data={indexCards}
            renderItem={({ item }) => (
              <TouchableOpacity onPress={() => pressHandler(item.key)}>
                <Text style={styles.indexCards}>{item.title}</Text>
              </TouchableOpacity>
            )}
          />

        </View>

        {/* <StatusBar style="auto" /> */}
      </View>
    );
  }
}

